import {Recipe} from "../recipeData"
import './recipePage.css'
import { useParams } from "react-router-dom";
import recipes from "../recipeData";
import React, { ChangeEvent, useState } from 'react';
import { isConstructorDeclaration } from "typescript";


    //finally found a way to deal with all of these annoying undefined issues,
    //we can use type scripts non null assertion operator to state that a value will never
    //be null, only use this when you know that what you are looking for will exist!




export default function RecipePage(){

    const [newIngredient, setNewIngredient] = useState('');
    const[newInstruction, setNewInstruction] = useState('');


    const { id } = useParams();


    let target = recipes.find(element => (element.name == id))!;

    const [allIngredients, setAllIngredients] = useState(target.ingredients);
    const [allInstructions, setAllInstructions] = useState(target.instructions);

    if(target == undefined){return (<>error!</>)}


    return(

        <main>

        <div className="card">

            <h1 className="title2">{target.name  }</h1>

            <div className="card-body">

                <img className="card-img" src={target.image} /> 

                <ul className="card-text"> 


                 {allIngredients.map(function(name, index) {
                return <li key={index}>{name}</li>;
                    })}


                    <input 
                        placeholder= "Some random step"
                        value = {newInstruction}
                        onChange={(e: ChangeEvent<HTMLInputElement>)  => {
                            setNewInstruction(e.target.value);

                        }}
                        />

                    <button onClick={() => setAllInstructions([...allInstructions, newInstruction ])}> 
                                New instruction
                    </button>


                </ul>
            </div>
        </div>

        <div className="card">

            <div className="preparations-title"> 

            <p>Steps</p>


            </div>

                         {/* was prepartions-body but couldnt find that in my original style sheet {*/}


            <ol className ="card-text">

                        {allIngredients.map(function(name, index) {
                            return <li key={index}>{name}</li>;
                        })}


                        <input
                            //placeholder is the default value that shows up 
                            placeholder = "3 cups of mystery juice"
                            //the value that is inputted will be stored in value, im guessing value is a variable associated with the <input> tag
                            value = {newIngredient}
                            //onChange keeps track of when input tags are updated/interacted with 
                            onChange={(e: ChangeEvent<HTMLInputElement>)  => {
                                setNewIngredient(e.target.value);

                            }}
                            />

                            {/*} needed to change on change to on click in order to for changes to occur
                            not sure if i messed someting up or if this was an issue on their part{*/}
                            <button onClick={() => {
                                setAllIngredients([...allIngredients, newIngredient])}}>

                            Add Ingredient
                            </button>

            </ol>


            </div>

            </main>







    )
}
