import {Recipe} from "../recipeData"
import './recipePage.css'
import { useParams } from "react-router-dom";
import recipes from "../recipeData";
import React, { ChangeEvent, useState } from 'react';



export default function RecipePage(){

    const [newIngredient, setNewIngredient] = useState('');

    const { id } = useParams();

    let target = recipes.find(element => (element.name == id));

    //i have no idea why, but the ? automatically appeared
    //seems useful since it helps deal with vscode errors due to the value possibly being undefined

    const[allIngredients, setAllIngredients] = useState(target?.ingredients)

    if(target == undefined){return (<>error!</>)}

    return(

        <main>



        <div className="card">


            <h1 className="title2">{target.name  }</h1>

            <div className="card-body">

                <img className="card-img" src={target.image} /> 

                <ul className="card-text"> 


                 {target.ingredients.map(function(name, index) {
                return <li key={index}>{name}</li>;
                    })}


                </ul>
            </div>
        </div>

        <div className="card">

            <div className="preparations-title"> 

            <p>Steps</p>


            </div>

                         {/* was prepartions-body but couldnt find that in my original style sheet {*/}


            <ol className ="card-text">

                        {target.ingredients.map(function(name, index) {
                            return <li key={index}>{name}</li>;
                        })}





                        <input 
                            placeholder = "3 cups of cheese"
                            //the value that is inputted will be stored in value, im guessing value is a variable associated with the <input> tag
                            value = {newIngredient}
                            //onChange keeps track of when input tags are updated/interacted with 
                            onChange={(e: ChangeEvent<HTMLInputElement>)  => {
                                setNewIngredient(e.target.value);

                            }}
                            />

            </ol>


            </div>

            </main>







    )
}



