import {Recipe} from "../recipeData"
import './recipePage.css'
import { useParams } from "react-router-dom";
import recipes from "../recipeData";
import React, { ChangeEvent, useEffect, useState } from 'react';
//import React, { PropTypes, Component } from 'react';


    //finally found a way to deal with all of these annoying undefined issues,
    //we can use type scripts non null assertion operator to state that a value will never
    //be null, only use this when you know that what you are looking for will exist!


    interface RecipePageProps {
        external?: boolean;
      }




export default function RecipePage(props:RecipePageProps){

    let target: Recipe;
    const { id } = useParams();

     // new data state variables
     const[newIngredient, setNewIngredient] = useState('');
     const[newInstruction, setNewInstruction] = useState('');
     const[curr_Recipe, setRecipe] = useState<Recipe>()
     console.log("id: ", id)
     console.log("props.external", props.external)
     if(false){console.log("hello")}

    useEffect(() => {
        if (props.external) {
            console.log("external props")

            fetch("https://bootcamp-milestone-4.onrender.com/recipe/:" + id)
            .then((recipes) => recipes.json())
            .then((data) => setRecipe(data));




          // make an API call with the url param & setRecipe
        } else {
          // query all of your recipe data for the recipe you want & setRecipe
          console.log("in here!!!!")
          let target = recipes.find(element => (element.name == id))!;
          setRecipe(target)

          console.log("local data")
        }
      }, [id, props.external]);


    //main state variables
    const [allIngredients, setAllIngredients] = useState(curr_Recipe!.ingredients);
    const [allInstructions, setAllInstructions] = useState(curr_Recipe!.instructions);


    return(




        <main>

        <div className="card">

            <h1 className="title2">{curr_Recipe!.name  }</h1>

            <div className="card-body">

                <img className="card-img" src={curr_Recipe!.image} /> 

                <ul className="card-text"> 


                    {/*}printing allInstruction list {*/}
                 {allInstructions.map(function(name, index) {
                return <li key={index}>{name}</li>;
                    })}


                    {/*} code responsbile for storing a new instruction from user input {*/}

                    <input 
                        placeholder= "Some random step"
                        value = {newInstruction}
                        onChange={(e: ChangeEvent<HTMLInputElement>)  => {
                            setNewInstruction(e.target.value);

                        }}
                        />

                        {/*} code responsbile for new instruction to allInstructions {*/}
                    <button onClick={() => setAllInstructions([...allInstructions, newInstruction ])}> 
                                Add instruction
                    </button>


                </ul>
            </div>
        </div>


        <div className="card">

            <div className="preparations-title"> 

            <p>Steps</p>

            </div>


            <ol className ="card-text">

                        {/*} printing all ingredients{*/}
                        {allIngredients.map(function(name, index) {
                            return <li key={index}>{name}</li>;
                        })}


                            {/*responsible for input box that stores a temp variable that is appended to a larger set {*/}
                                                    <input
                            placeholder = "3 cups of mystery juice"
                            //the value that is inputted will be stored in value, im guessing value is a variable associated with the <input> tag
                            value = {newIngredient}
                            //onChange keeps track of when input tags are updated/interacted with 
                            onChange={(e: ChangeEvent<HTMLInputElement>)  => {
                                setNewIngredient(e.target.value);

                            }}
                            />

                            {/*} needed to change on change to on click in order to for changes to occur
                            not sure if i messed someting up or if this was an issue on their part{*/}
                            <button onClick={() => {
                                setAllIngredients([...allIngredients, newIngredient])}}>

                            Add Ingredient
                            </button>

            </ol>
            </div>
            </main>             

    )
                            }



RecipePage.defaultProps = {
    external: false
  };
